@prefix poli: <http://purl.org/NET/polian#> .
@prefix dcterms: <http://purl.org/dc/terms/> .

<http://data.europa.eu/eli/dir/2004/8/oj> 
    a poli:Legislation ;
    dcterms:title "EU_32004L0008_Title_0_Chapter_0_Section_0_Article_10" ;
    dcterms:identifier "32004L0008" .

<poli:EUMemberState>
    a poli:Country ;
    poli:hasObligation [
        a poli:Action ;
        poli:refersTo <http://data.europa.eu/eli/dir/2004/8/oj> ;
        poli:number "1" ;
        poli:text "publish a report with the results of the analysis and evaluations carried out in accordance with articles 5(3), 6(1), 9(1) and 9(2)" ;
        poli:time [
            a poli:Time_Monitoring ;
            poli:annotatedText "not later than 21 february 2006"
        ]
    ] ;
    poli:hasObligation [
        a poli:Action ;
        poli:refersTo <http://data.europa.eu/eli/dir/2004/8/oj> ;
        poli:number "2" ;
        poli:text "publish a report with the result of the evaluation referred to in article 6(3)" ;
        poli:time [
            a poli:Time_Monitoring ;
            poli:annotatedText "not later than 21 february 2007 and thereafter every four years"
        ]
    ] ;
    poli:hasObligation [
        a poli:Action ;
        poli:refersTo <http://data.europa.eu/eli/dir/2004/8/oj> ;
        poli:number "3" ;
        poli:text "submit to the commission, for the first time before the end of december 2004 covering data for the year 2003, and thereafter on an annual basis, statistics on national electricity and heat production from cogeneration, in accordance with the methodology shown in annex ii"
    ] ;
    poli:hasObligation [
        a poli:Action ;
        poli:refersTo <http://data.europa.eu/eli/dir/2004/8/oj> ;
        poli:number "3" ;
        poli:text "submit annual statistics on cogeneration capacities and fuels used for cogeneration"
    ] ;
    poli:hasObligation [
        a poli:Action ;
        poli:refersTo <http://data.europa.eu/eli/dir/2004/8/oj> ;
        poli:number "3" ;
        poli:text "submit statistics on primary energy savings achieved by application of cogeneration, in accordance with the methodology shown in annex iii"
    ] .

