@prefix dc: <http://purl.org/dc/elements/1.1/> .
@prefix dct: <http://purl.org/dc/terms/> .
@prefix polianna: <https://www.w3id.org/poli/def/poliana#> .
@prefix prov: <http://www.w3.org/ns/prov#> .
@prefix skos: <http://www.w3.org/2004/02/skos/core#> .

<https://w3id.org/polifonia/example/article5>
  a polianna:Article ;
  dc:title "Article 5" ;
  dct:description """Text of Article 5""" ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 1" ;
    dct:description """When implementing measures for the comprehensive renovation of central government buildings in accordance with the first subparagraph, member states may choose to consider the building as a whole, including the building envelope, equipment, operation and maintenance.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 1" ;
      dct:description """When implementing measures for the comprehensive renovation of central government buildings in accordance with the first subparagraph, member states may choose to consider the building as a whole, including the building envelope, equipment, operation and maintenance.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 2" ;
    dct:description """Member States may decide not to set or apply the requirements referred to in paragraph 1 to the following categories of buildings:""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 2.1" ;
      dct:description """(a) Buildings officially protected as part of a designated environment, or because of their special architectural or historical merit, in so far as compliance with certain minimum energy performance requirements would unacceptably alter their character or appearance;""" ;
    ] ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 2.2" ;
      dct:description """(b) Buildings owned by the armed forces or central government and serving national defence purposes, apart from single living quarters or office buildings for the armed forces and other staff employed by national defence authorities;""" ;
    ] ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 2.3" ;
      dct:description """(c) Buildings used as places of worship and for religious activities.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 3" ;
    dct:description """If a Member State renovates more than 3 % of the total floor area of central government buildings in a given year, it may count the excess towards the annual renovation rate of any of the three previous or following years.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 3" ;
      dct:description """If a Member State renovates more than 3 % of the total floor area of central government buildings in a given year, it may count the excess towards the annual renovation rate of any of the three previous or following years.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 4" ;
    dct:description """Member States may count towards the annual renovation rate of central government buildings new buildings occupied and owned as replacements for specific central government buildings demolished in any of the two previous years, or buildings that have been sold, demolished or taken out of use in any of the two previous years due to more intensive use of other buildings.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 4" ;
      dct:description """Member States may count towards the annual renovation rate of central government buildings new buildings occupied and owned as replacements for specific central government buildings demolished in any of the two previous years, or buildings that have been sold, demolished or taken out of use in any of the two previous years due to more intensive use of other buildings.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 5" ;
    dct:description """For the purposes of paragraph 1, by 31 December 2013, Member States shall establish and make publicly available an inventory of heated and/or cooled central government buildings with a total useful floor area over 500 m2 and, as of 9 July 2015, over 250 m2, excluding buildings exempted on the basis of paragraph 2.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 5" ;
      dct:description """For the purposes of paragraph 1, by 31 December 2013, Member States shall establish and make publicly available an inventory of heated and/or cooled central government buildings with a total useful floor area over 500 m2 and, as of 9 July 2015, over 250 m2, excluding buildings exempted on the basis of paragraph 2.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 6" ;
    dct:description """Member States may encourage the use of renewable energy sources in new buildings and existing buildings undergoing major renovation, inter alia by providing information to building owners and tenants on the technical feasibility and potential economic viability of such installations.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 6" ;
      dct:description """Member States may encourage the use of renewable energy sources in new buildings and existing buildings undergoing major renovation, inter alia by providing information to building owners and tenants on the technical feasibility and potential economic viability of such installations.""" ;
    ] ;
  ] ;

  polianna:hasSection [
    a polianna:Section ;
    dc:title "Paragraph 7" ;
    dct:description """Member States may apply more stringent requirements than those laid down in this Directive, inter alia by setting minimum energy performance requirements that are more ambitious than those required by Annex I.""" ;
    polianna:hasSentence [
      a polianna:Sentence ;
      dc:title "Sentence 7" ;
      dct:description """Member States may apply more stringent requirements than those laid down in this Directive, inter alia by setting minimum energy performance requirements that are more ambitious than those required by Annex I.""" ;
    ] ;
  ] ;

  prov:wasDerivedFrom <https://w3id.org/polifonia/example/source> .

