@prefix pol: <http://example.org/poliaffa/ontologies/POLIANNA#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

pol:Article_7 a pol:Regulation ;
    pol:title "Energy Efficiency Obligation Schemes and Alternative Policy Measures" ;
    pol:dateIssued "2023-04-15"^^xsd:date ;
    pol:description "This regulation outlines the requirements for energy efficiency obligation schemes and alternative policy measures to achieve the required level of ambition in reducing end-use energy consumption." .

pol:Requirement_7.1 a pol:RegulationItem ;
    pol:title "Energy Efficiency Obligation Schemes" ;
    pol:regulates pol:Article_7 ;
    pol:description "Member states must implement obligation schemes leading to reductions in final or primary energy use by 2020 with specified percentage levels." .

pol:Requirement_7.2 a pol:RegulationItem ;
    pol:title "Reduction Levels" ;
    pol:regulates pol:Article_7 ;
    pol:description "Percentage reduction targets for the period 2014 to 2020 are defined, including specific rates for each year." .

pol:Requirement_7.3 a pol:RegulationItem ;
    pol:title "Alternative Policy Measures" ;
    pol:regulates pol:Article_7 ;
    pol:description "Member states may use alternative policy measures if they demonstrate equivalence in achieving required energy savings, monitoring, and verification." .

pol:PolicyMeasure_Taxes a pol:RegulationSubItem ;
    pol:title "Energy or CO2 Taxes" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Policies involving taxes that reduce end-use energy consumption are considered with specified criteria for compliance." .

pol:PolicyMeasure_Financing a pol:RegulationSubItem ;
    pol:title "Financing Schemes and Instruments" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Financial tools or incentives leading to application of energy-efficient technology or techniques are included as policy measures." .

pol:PolicyMeasure_Regulations a pol:RegulationSubItem ;
    pol:title "Regulations and Voluntary Agreements" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Policies involving regulations or voluntary agreements leading to energy-efficient technology adoption are considered with specified criteria for compliance." .

pol:PolicyMeasure_Norms a pol:RegulationSubItem ;
    pol:title "Standards and Norms" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Policies involving standards or norms aiming at improving energy efficiency of products, services, buildings, and vehicles are included as policy measures." .

pol:PolicyMeasure_Labeling a pol:RegulationSubItem ;
    pol:title "Energy Labeling Schemes" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Policies involving energy labeling schemes are considered with specified criteria for compliance." .

pol:PolicyMeasure_Training a pol:RegulationSubItem ;
    pol:title "Training and Education Programs" ;
    pol:regulates pol:Requirement_7.3 ;
    pol:description "Programs that lead to application of energy-efficient technology or techniques through training and education are included as policy measures." .

pol:Criteria_10 a pol:RegulationCriteria ;
    pol:title "Criteria for Policy Measures" ;
    pol:regulatedBy pol:Article_7 ;
    pol:description "Defines criteria such as achieving ambition levels, responsibility definition, transparent savings calculation, use of energy consumption expressions, methods and principles for saving calculations, annual reporting, monitoring, control systems with independent verification." .

pol:Criteria_Taxes a pol:RegulationCriteriaSubItem ;
    pol:title "Taxes Criteria" ;
    pol:regulatedBy pol:Criteria_10 ;
    pol:description "Tax policies must comply with specified criteria including ambition levels, responsibility definition, transparent savings calculation, use of energy consumption expressions, monitoring, and data publication." .

pol:Criteria_Regulations a pol:RegulationCriteriaSubItem ;
    pol:title "Regulations Criteria" ;
    pol:regulatedBy pol:Criteria_10 ;
    pol:description "Regulatory policies must comply with specified criteria including ambition levels, responsibility definition, transparent savings calculation, use of energy consumption expressions, methods and principles for saving calculations, annual reporting, monitoring, control systems with independent verification." .

pol:Criteria_Other a pol:RegulationCriteriaSubItem ;
    pol:title "Other Policy Measures Criteria" ;
    pol:regulatedBy pol:Criteria_10 ;
    pol:description "Other policy measures must comply with specified criteria including ambition levels, responsibility definition, transparent savings calculation, use of energy consumption expressions, monitoring, control systems with independent verification." .

