    poliana:hasCriterion [
      a poliana:Criterion ;
      poliana:text "energy savings are calculated using the methods and principles provided in point 3 of Annex V"
    ] ;
    poliana:hasCriterion [
      a poliana:Criterion ;
      poliana:text "monitoring of the results is ensured and appropriate measures are envisaged if the progress is not satisfactory"
    ]
  ] .

<https://w3id.org/polifonia/example/article>
