@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix skos: <http://www.w3.org/2004/02/skos/core#> .
@prefix dc: <http://purl.org/dc/terms/> .
@prefix prov: <http://www.w3.org/ns/prov#> .

:Article rdf:type owl:Ontology ;
    dc:title "Energy Efficiency Directive" ;
    dc:description "This article outlines the requirements for energy efficiency in the EU." ;
    skos:notation "2012/27/EU" .

:Paragraph1 rdf:type owl:Axiom ;
    dc:title "General Requirements" ;
    dc:description "Member States shall achieve a 20% reduction in primary energy consumption by 2020." ;
    :hasRequirement [ rdf:value "20%" ] .

:Article :hasPart :Paragraph1, :Paragraph2, :Paragraph3, :Paragraph4, :Paragraph5, :Paragraph6, :Paragraph7, :Paragraph8, :Paragraph9, :Paragraph10, :Paragraph11, :Paragraph12 .

:Paragraph2 rdf:type owl:Axiom ;
    dc:title "National Action Plans" ;
    dc:description "Member States shall submit National Action Plans to the Commission by 30 April 2014." .

:Paragraph3 rdf:type owl:Axiom ;
    dc:title "Energy Efficiency Obligation Schemes" ;
    dc:description "Member States may implement Energy Efficiency Obligation Schemes to achieve energy savings." .

:Paragraph4 rdf:type owl:Axiom ;
    dc:title "Alternative Measures" ;
    dc:description "Member States may implement alternative measures, such as taxation or financing schemes, to achieve energy savings." .

:Paragraph5 rdf:type owl:Axiom ;
    dc:title "Energy Efficiency National Funds" ;
    dc:description "Member States shall establish Energy Efficiency National Funds to support energy efficiency projects." .

:Paragraph6 rdf:type owl:Axiom ;
    dc:title "Monitoring and Verification" ;
    dc:description "Member States shall ensure the monitoring and verification of energy savings achieved through policy measures." .

:Paragraph7 rdf:type owl:Axiom ;
    dc:title "Annual Reports" ;
    dc:description "Member States shall submit annual reports to the Commission on the progress made in achieving energy savings." .

:Paragraph8 rdf:type owl:Axiom ;
    dc:title "Control System" ;
    dc:description "Member States shall establish a control system to ensure the effective implementation of policy measures." .

:Paragraph9 rdf:type owl:Axiom ;
    dc:title "Policy Measures" ;
    dc:description "Member States may implement policy measures, such as energy taxes or regulations, to achieve energy savings." .

:Paragraph10 rdf:type owl:Axiom ;
    dc:title "Criteria for Policy Measures" ;
    dc:description "Policy measures shall meet certain criteria, including transparency and monitoring requirements." .

:Paragraph11 rdf:type owl:Axiom ;
    dc:title "Specific Criteria for Taxes and Regulations" ;
    dc:description "Taxes and regulations shall comply with specific criteria, including the requirement to reduce end-use energy consumption." .

:Paragraph12 rdf:type owl:Axiom ;
    dc:title "No Double Counting" ;
    dc:description "Member States shall ensure that no double counting of energy savings is made when policy measures or individual actions overlap." .
```

