    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "retail energy sales company" ;
        pol:description "means an undertaking which sells or offers for sale energy to final customers."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "gas transmission system operator" ;
        pol:description "means an undertaking that operates a gas transmission network and manages the distribution of gas through such network."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "individual action" ;
        pol:description "means any measure or set of measures taken by an obligated party to improve energy efficiency."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "energy distributor" ;
        pol:description "means an undertaking which distributes, but not sells, energy to final customers through a network owned and/or operated by that undertaking."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "starch-rich crops" ;
        pol:description "means crops with high starch content such as maize, wheat, barley, triticale, oats and rye."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "heat pump" ;
        pol:description "means a device or installation that uses low-grade energy (e.g. from waste heat, ambient air, ground or water) to transfer heat from one location to another."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "cogeneration" ;
        pol:description "means the simultaneous generation of thermal and electrical energy in a single process."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "energy service provider" ;
        pol:description "means an undertaking which delivers energy services or other energy efficiency improvement measures to end-users, including those undertakings that install and/or maintain and/or upgrade the equipment necessary for energy efficiency improvements."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "obligated party" ;
        pol:description "means any public or private undertaking which has to meet specific energy efficiency requirements as set by this Directive."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "primary energy consumption" ;
        pol:description "means the gross inland consumption of energy, except for non-energy uses such as chemical processes."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "ligno-cellulosic material" ;
        pol:description "means any plant-derived organic matter exclusive of starch-rich crops and sugars."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "fossil fuel" ;
        pol:description "means non-renewable carbon-based energy sources such as solid fuels, natural gas and oil."@en
    ] ;
    pol:hasDefinition [
        rdf:type pol:Definition ;
        pol:term "building" ;
        pol:description "means a roofed construction having walls, for which energy is used to condition the indoor climate; this includes also any building unit that has been designed or altered to have one or more of its walls in common with another such unit."@en
    ] .

