pol:32018R1999 a pol:PolicyDocument ;
    pol:hasArticle pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04 .

pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04 a pol:PolicyArticle ;
    pol:contains_objective pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04_ObjDecarbonisation,
                           pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04_ObjRenewableEnergy,
                           pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04_ObjEnergyEfficiency,
                           pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04_ObjEnergySecurity,
                           pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_04_ObjInternalMarket,
