    pol:hasSection ex:Section_9 ;
    pol:hasSection ex:Section_10 .

ex:Section_7 a pol:LegislativeSection ;
    pol:title "Analysis and action plan on energy from renewable sources" ;
    pol:dueDate "2010-12-31"^^xsd:date ;
    pol:containsPoint ex:Point_7a ;
    pol:containsPoint ex:Point_7b ;
    pol:containsPoint ex:Point_7c ;
    pol:containsPoint ex:Point_7d ;
