pol:EU_32018R1999_Title_0_Chapter_2_Section_0_Article_14_Paragraph_1 a pol:LegalActParagraph ;
    pol:text "by 30 june 2023, and subsequently by 1 january 2033 and every 10 years thereafter, each member state shall submit to the commission a draft update of the latest notified integrated national energy and climate plan or shall provide the commission with reasons justifying why the plan does not require updating." ;
    pol:hasDate "2023-06-30"^^xsd:date , "2033-01-01"^^xsd:date .

