    pol:regulates pol:Article_7 ;
    pol:description "Percentage reduction targets for the period 2014 to 2020 are defined, including specific rates for each year." .

pol:Requirement_7.3 a pol:RegulationItem ;
    pol:title "Alternative Policy Measures" ;
