  "eli:has_part": [
    {
      "@id": "#article_10_paragraph_1",
      "@type": "polianna:Paragraph",
      "dct:description": "Member states shall calculate the collection rate for the first time in respect of the fifth full calendar year following the entry into force of this directive. Without prejudice to Directive 2002/96/EC, annual collection and sales figures shall include batteries and accumulators incorporated into appliances."
    },
    {
      "@id": "#article_10_paragraph_2",
      "@type": "polianna:Paragraph",
      "dct:description": "Member states shall achieve the following minimum collection rates:",
