        {
          "@id": "#time_compliance_25_percent",
          "@type": "polianna:Time_Compliance",
          "polianna:Compliance_date": "2012-09-26",
          "dct:description": "25 % by 26 September 2012"
        },
        {
          "@id": "#time_compliance_45_percent",
          "@type": "polianna:Time_Compliance",
          "polianna:Compliance_date": "2016-09-26",
          "dct:description": "45 % by 26 September 2016"
        }
      ]
    },
    {
      "@id": "#article_10_paragraph_3",
      "@type": "polianna:Paragraph",
      "dct:description": "Member states shall monitor collection rates on a yearly basis according to the scheme set out in Annex I. Without prejudice to Regulation (EC) No 2150/2002 of the European Parliament and of the Council of 25 November 2002 on waste statistics, Member States shall transmit reports to the Commission within six months of the end of the calendar year concerned. Reports shall indicate how they obtained the data necessary to calculate the collection rate.",
      "polianna:contains_monitoring_time": {
        "@id": "#monitoring_time_yearly",
        "@type": "polianna:Time_Monitoring",
        "dct:description": "Yearly basis"
      }
    },
    {
      "@id": "#article_10_paragraph_4",
      "@type": "polianna:Paragraph",
      "dct:description": "In accordance with the procedure referred to in Article 24(2):",
      "polianna:specifies_instrument": [
        {
          "@id": "#instrument_transitional_arrangements",
          "@type": "skos:Concept",
          "skos:prefLabel": "Transitional Arrangements",
          "dct:description": "Transitional arrangements may be laid down to address difficulties faced by a Member State in satisfying the requirements of paragraph 2 as a result of specific national circumstances."
        },
        {
          "@id": "#instrument_common_methodology",
          "@type": "skos:Concept",
          "skos:prefLabel": "Common Methodology",
          "dct:description": "A common methodology shall be established for the calculation of annual sales of portable batteries and accumulators to end-users by 26 September 2007.",
          "polianna:specifies_time": {
            "@id": "#time_common_methodology",
            "@type": "polianna:Time_Compliance",
            "polianna:Compliance_date": "2007-09-26"
          }
        }
      ]
    }
  ],
  "eli:is_about": [
    {
      "@id": "#concept_collection_rate",
      "@type": "skos:Concept",
      "skos:prefLabel": "Collection Rate"
    },
    {
      "@id": "#concept_batteries_and_accumulators",
      "@type": "skos:Concept",
      "skos:prefLabel": "Batteries and Accumulators"
    }
  ]
}
