{
  "directive_title": "Energy Efficiency Directive",
  "objective": "To improve energy efficiency in buildings and reduce greenhouse gas emissions.",
  "scope": "Applies to all new buildings and existing buildings undergoing major renovations within the European Union.",
  "requirements": [
    {
      "section": "Building Design and Construction",
      "details": [
        "All new buildings must meet minimum energy performance standards (MEPS).",
        "Buildings undergoing major renovations must also comply with MEPS for the renovated parts."
      ]
    },
    {
      "section": "Energy Performance Certificates",
      "details": [
        "Energy performance certificates (EPCs) must be issued for all buildings.",
        "EPCs must include information on energy consumption, efficiency measures, and potential savings."
      ]
    },
    {
      "section": "Renovation Obligations",
      "details": [
        "Buildings with an EPC rating below a certain threshold must undergo renovations to improve their energy performance.",
        "The specific requirements for these renovations are outlined in the directive."
      ]
    },
    {
      "section": "Monitoring and Reporting",
      "details": [
        "Member States must monitor and report on progress towards meeting energy efficiency targets.",
        "Annual reports must be submitted to the European Commission detailing actions taken and results achieved."
      ]
    }
  ],
  "compliance_and_enforcement": {
    "monitoring": "The European Commission will regularly monitor compliance with the directive.",
    "enforcement": "Enforcement action may include fines or other penalties for non-compliance.",
    "appeals": "Member States have the right to appeal any enforcement decisions."
  }
}

