jsonld
{
  "@context": {
    "schema": "http://schema.org/",
    "piana": "https://piana.example.com/ontology#"
  },
  "@type": "piana:Article",
  "schema:name": "Article on Renewable Fuels for Transport",
  "schema:description": "This article outlines measures to ensure the availability of fuels from renewable sources for transport, including requirements for data entry into a Union database and methodologies for assessing greenhouse gas emissions savings.",
  "piana:hasSection": [
    {
      "@type": "piana:Section",
      "schema:name": "Data Entry Requirements",
      "schema:description": "Requirements for fuel suppliers to enter information necessary to verify compliance with sustainability criteria into the relevant database.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "Fuel suppliers shall enter the information necessary to verify compliance with the requirements laid down in the first and fourth subparagraphs of article 25(1) into the relevant database."
        }
      ]
    },
    {
      "@type": "piana:Section",
      "schema:name": "Measures for Availability of Renewable Fuels",
      "schema:description": "Member states are required to take measures to ensure the availability of fuels from renewable sources for transport by 31 December 2021.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "By 31 december 2021, member states shall take measures to ensure the availability of fuels from renewable sources for transport including with regard to publicly accessible high-power recharging points and other refuelling infrastructure as provided for in their national policy frameworks in accordance with directive 2014/94/eu."
        }
      ]
    },
    {
      "@type": "piana:Section",
      "schema:name": "Access to Union Database",
      "schema:description": "Member states shall have access to the union database and take measures to ensure accurate information entry.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "Member states shall have access to the union database referred to in paragraph 2 of this article. They shall take measures to ensure that economic operators enter accurate information into the relevant database."
        }
      ]
    },
    {
      "@type": "piana:Section",
      "schema:name": "Delegated Acts for Methodology Specification",
      "schema:description": "The commission is required to adopt delegated acts specifying methodologies for assessing greenhouse gas emissions savings.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "By 31 december 2021, the commission shall adopt delegated acts in accordance with article 35 to supplement this directive by specifying the methodology to determine the share of biofuel, and biogas for transport, resulting from biomass being processed with fossil fuels in a common process, and by specifying the methodology for assessing greenhouse gas emissions savings from renewable liquid and gaseous transport fuels of non-biological origin and from recycled carbon fuels, which shall ensure that credit for avoided emissions is not given for co2 the capture of which has already received an emission credit under other provisions of law."
        }
      ]
    },
    {
      "@type": "piana:Section",
      "schema:name": "Review of Feedstock List",
      "schema:description": "The commission is empowered to adopt delegated acts in accordance with article 35 to amend the list of feedstock set out in parts a and b of annex ix.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "By 25 june 2019 and every two years thereafter, the commission shall review the list of feedstock set out in parts a and b of annex ix with a view to adding feedstock in accordance with the principles set out in the third subparagraph."
        }
      ]
    },
    {
      "@type": "piana:Section",
      "schema:name": "Assessment of Advanced Biofuels Obligation",
      "schema:description": "The commission shall assess whether the obligation relating to advanced biofuels and biogas produced from feedstock listed in part a of annex ix effectively stimulates innovation and ensures greenhouse gas emissions savings.",
      "piana:containsParagraph": [
        {
          "@type": "piana:Paragraph",
          "schema:text": "By 31 december 2025, in the context of the biennial assessment of progress made pursuant to regulation (eu) 2018/1999, the commission shall assess whether the obligation relating to advanced biofuels and biogas produced from feedstock listed in part a of annex ix effectively stimulates innovation and ensures greenhouse gas emissions savings."
        }
      ]
    }
  ],
  "piana:hasRequirement": [
    {
      "@type": "piana:Requirement",
      "schema:name": "Data Entry Requirement",
      "schema:description": "Fuel suppliers must enter information into the relevant database to verify compliance with sustainability criteria.",
      "piana:deadline": null
    },
    {
      "@type": "piana:Requirement",
      "schema:name": "Availability of Renewable Fuels",
      "schema:description": "Member states must ensure availability of fuels from renewable sources for transport by 31 December 2021.",
      "piana:deadline": "2021-12-31"
    },
    {
      "@type": "piana:Requirement",
      "schema:name": "Access to Union Database",
      "schema:description": "Member states must have access to the union database and ensure accurate information entry.",
      "piana:deadline": null
    },
    {
      "@type": "piana:Requirement",
      "schema:name": "Methodology Specification",
      "schema:description": "The commission must adopt delegated acts specifying methodologies for assessing greenhouse gas emissions savings by 31 December 2021.",
      "piana:deadline": "2021-12-31"
    },
    {
      "@type": "piana:Requirement",
      "schema:name": "Review of Feedstock List",
      "schema:description": "The commission must review the list of feedstock set out in parts a and b of annex ix by 25 June 2019 and every two years thereafter.",
      "piana:deadline": "2019-06-25"
    },
    {
      "@type": "piana:Requirement",
      "schema:name": "Assessment of Advanced Biofuels Obligation",
      "schema:description": "The commission must assess whether the obligation relating to advanced biofuels and biogas produced from feedstock listed in part a of annex ix effectively stimulates innovation and ensures greenhouse gas emissions savings by 31 December 2025.",
      "piana:deadline": "2025-12-31"
    }
  ],
  "piana:hasMethodology": [
    {
      "@type": "piana:Methodology",
      "schema:name": "Methodology for Determining Share of Biofuel and Biogas",
      "schema:description": "The commission must specify the methodology to determine the share of biofuel, and biogas for transport, resulting from biomass being processed with fossil fuels in a common process."
    },
    {
      "@type": "piana:Methodology",
      "schema:name": "Methodology for Assessing Greenhouse Gas Emissions Savings",
      "schema:description": "The commission must specify the methodology for assessing greenhouse gas emissions savings from renewable liquid and gaseous transport fuels of non-biological origin and from recycled carbon fuels."
    }
  ],
  "piana:hasReviewProcess": [
    {
      "@type": "piana:ReviewProcess",
      "schema:name": "Review of Feedstock List",
      "schema:description": "The commission must review the list of feedstock set out in parts a and b of annex ix with a view to adding feedstock in accordance with the principles set out in the third subparagraph.",
      "piana:schedule": "Every two years"
    }
  ],
  "piana:hasAssessmentProcess": [
    {
      "@type": "piana:AssessmentProcess",
      "schema:name": "Assessment of Advanced Biofuels Obligation",
      "schema:description": "The commission must assess whether the obligation relating to advanced biofuels and biogas produced from feedstock listed in part a of annex ix effectively stimulates innovation and ensures greenhouse gas emissions savings.",
      "piana:deadline": "2025-12-31"
    }
  ]
}
