      {
        "@type": "eli:Paragraph",
        "eli:number": "1",
        "dct:description": "by 31 october of every year, the commission shall submit to the european parliament and to the council a state of the energy union report."
      },
      {
        "@type": "eli:Paragraph",
        "eli:number": "2",
        "dct:description": "the state of the energy union report shall include the following elements:",
        "poliana:hasSubparagraph": [
          {
            "@type": "eli:Subparagraph",
            "eli:number": "a",
            "dct:description": "the assessment carried out pursuant to article 29;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": ",
            dct:description": "where appropriate, recommendations pursuant to article 34;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "c",
            "dct:description": "the report on the functioning of the carbon market referred to in article 10(5) of directive 2003/87/ec, including information on the application of that directive in accordance with article 21(2) thereof;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "d",
            "dct:description": "biennially, from 2023, a report on union bioenergy sustainability, containing the information specified in annex x;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "e",
            "dct:description": "biennially, a report on voluntary schemes in respect of which the commission has adopted a decision pursuant to article 30(4) of directive (eu) 2018/2001, containing the information specified in annex xi to this regulation;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "f",
            "dct:description": "an overall progress report on the application of directive 2009/72/ec;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "g",
            "dct:description": "an overall progress report on the application of directive 2009/73/ec pursuant to article 52 of that directive;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "h",
            "dct:description": "an overall progress report on energy efficiency obligation schemes and alternative policy measures as referred to in articles 7a and 7b of directive 2012/27/eu;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "i",
            "dct:description": "biennially, an overall progress report on the renovation of the national stock of residential and non-residential buildings, both public and private, in line with the roadmaps set out in the long-term renovation strategies that each member state shall establish in accordance with article 2a of directive 2010/31/eu;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "j",
            "dct:description": "every four years, an overall progress report on member states' increase in the number of nearly zero-energy buildings in accordance with article 9(5) of directive 2010/31/eu;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "k",
            "dct:description": "an overall progress report on member states' progress in creating a complete and operational energy market;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "l",
            "dct:description": "actual fuel quality in the different member states and geographical coverage of fuels with a maximum sulphur content of 10 mg/kg aiming to provide an overview of the fuels quality data in the different member states as reported pursuant to directive 98/70/ec;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "m",
            "dct:description": "poliana:hasSubparagraphan overall progress report on the competitiveness of the european energy market, including information on prices and availability of energy sources;"
          },
          {
            "@type": "eli:Subparagraph",
            "eli:number": "n",
            "dct:description": "poliana:hasSubparagraphan analysis of the impact of climate change on the european energy sector and recommendations for adaptation measures;"
          }
        ]
      }
    ]
  }
}

