  "@type": "poliana:Article",
  "dct:title": "EU_32018R1999_Title_0_Chapter_7_Section_3_Article_58",
  "eli:number": "32018R1999",
  "eli:hasExpression": {
    "@type": "eli:LegalResource",
    "eli:expressionDate": "2018-12-19",
