  "poliana:hasParagraph": [
    {
      "@type": "poliana:Paragraph",
      "dct:title": "Transitional provisions",
      "poliana:text": "By way of derogation from article 57 of this regulation, article 7 and points (a) and (d) of article 17(1) of regulation (eu) no 525/2013 shall continue to apply to the reports containing the data required under those articles for the years 2018, 2019 and 2020."
    },
    {
      "@type": "poliana:Paragraph",
      "poliana:text": "Article 11(3) of regulation (eu) no 525/2013 shall continue to apply as regards the second commitment period of the kyoto protocol."
