  "@type": "eli:Article",
  "eli:title": "EU_32004L0008_Title_0_Chapter_0_Section_0_Article_10",
  "eli:number": "10",
  "eli:hasCELEXNumber": "32004L0008",
  "eli:isRealizedBy": {
    "@type": "eli:LegalResource",
    "eli:expressionDate": "2004-08-01",
    "eli:language": "en",
    "dct:source": "http://data.europa.eu/eli/dir/2004/8/oj"
  },
  "poliana:hasParagraph": [
    {
      "@type": "poliana:Paragraph",
      "poliana:paragraphNumber": "1",
      "poliana:text": "Member States shall, not later than 21 February 2006, publish a report with the results of the analysis and evaluations carried out in accordance with Articles 5(3), 6(1), 9(1) and 9(2)."
    },
    {
      "@type": "poliana:Paragraph",
      "poliana:paragraphNumber": "2",
      "poliana:text": "Member States shall not later than 21 February 2007 and thereafter every four years, following a request by the Commission at least six months before the due date, publish a report with the result of the evaluation referred to in Article 6(3)."
    },
    {
      "@type": "poliana:Paragraph",
      "poliana:paragraphNumber": "3",
      "poliana:text": "Member States shall submit to the Commission, for the first time before the end of December 2004 covering data for the year 2003, and thereafter on an annual basis, statistics on national electricity and heat production from cogeneration, in accordance with the methodology shown in Annex II. They shall also submit annual statistics on cogeneration capacities and fuels used for cogeneration. Member States may also submit statistics on primary energy savings achieved by application of cogeneration, in accordance with the methodology shown in Annex III."
    }
  ]
}
