        "@type": "poliana:LegalProvisionParagraph",
        "dct:title": {
          "@value": "2.",
          "@language": "en"
        },
        "dct:description": {
          "@value": "the commission is empowered to adopt delegated acts in accordance with article 17 in order to supplement this regulation by establishing the measures referred to in paragraph 1 of this article.",
          "@language": "en"
        }
      }
    ]
  }
}
