      "eli:hasLocator": [
        {
          "@type": "eli:Locator",
          "eli:schemeName": "ELI",
          "eli:keyValue": "http://data.europa.eu/eli/reg/2019/631/oj"
        }
      ]
    }
  ],
  "poliana:hasAnnotator": [
    {
      "@type": "poliana:Person",
      "dct:identifier": "F"
    },
    {
      "@type": "poliana:Person",
      "dct:identifier": "B"
    }
  ],
  "poliana:containsArticle": {
    "@type": "poliana:LegalProvision",
    "dct:title": {
      "@value": "article 14",
      "@language": "en"
    },
    "dct:description": {
      "@value": "adjustment of m0 and tm0 values",
      "@language": "en"
    },
    "poliana:hasParagraph": [
      {
        "@type": "poliana:LegalProvisionParagraph",
        "dct:title": {
          "@value": "1.",
          "@language": "en"
        },
        "dct:description": {
          "@value": "the m0 and tm0 values referred to in parts a and b of annex i shall be adjusted as follows:",
          "@language": "en"
        },
        "poliana:hasSubParagraph": [
          {
            "@type": "poliana:LegalProvisionSubParagraph",
            "dct:title": {
              "@value": "(a)",
              "@language": "en"
            },
            "dct:description": {
              "@value": "by 31 october 2020, the m0 value in point 4 of part a of annex i shall be adjusted to the average mass in running order of all new passenger cars registered in 2017, 2018, and 2019. that new m0 value shall apply from 1 january 2022 until 31 december 2024;",
              "@language": "en"
            }
          },
          {
            "@type": "poliana:LegalProvisionSubParagraph",
            "dct:title": {
              "@value": "(b)",
              "@language": "en"
            },
            "dct:description": {
              "@value": "by 31 october 2022, the m0 value in point 4 of part b of annex i shall be adjusted to the average mass in running order of all new light commercial vehicles registered in 2019, 2020 and 2021. that new m0 value shall apply in 2024;",
              "@language": "en"
            }
          },
          {
            "@type": "poliana:LegalProvisionSubParagraph",
            "dct:title": {
              "@value": "(c)",
              "@language": "en"
            },
            "dct:description": {
              "@value": "by 31 october 2022, the indicative tm0 value for 2025 shall be determined as the respective average test mass of all new passenger cars and new light commercial vehicles registered in 2021;",
              "@language": "en"
            }
          },
          {
            "@type": "poliana:LegalProvisionSubParagraph",
            "dct:title": {
              "@value": "(d)",
              "@language": "en"
            },
            "dct:description": {
              "@value": "by 31 october 2024, and every second year thereafter, the tm0 value in point 6.2 of parts a and b of annex i shall be adjusted to the respective average test mass of all new passenger cars and new light commercial vehicles registered in the preceding two calendar years, starting with 2022 and 2023. the new tm0 values shall apply from 1 january of the calendar year following the date of the adjustment.",
              "@language": "en"
            }
          }
        ]
      },
      {
        "@type": "poliana:LegalProvisionParagraph",
        "dct:title": {
          "@value": "2.",
          "@language": "en"
        },
        "dct:description": {
          "@value": "the commission is empowered to adopt delegated acts in accordance with article 17 in order to supplement this regulation by establishing the measures referred to in paragraph 1 of this article.",
          "@language": "en"
        }
      }
    ]
  }
}
