  "eli:number": "32018L2001",
  "eli:hasExpression": {
    "@type": "eli:LegalResource",
    "eli:expressionDate": "2018-12-19",
    "dct:source": "http://data.europa.eu/eli/dir/2018/2001/oj"
  },
  "poliana:hasAnnotator": [
    {
      "@type": "poliana:Person",
      "dct:identifier": "C"
    },
    {
      "@type": "poliana:Person",
      "dct:identifier": "F"
    }
  ],
  "poliana:hasArticle": {
    "@type": "poliana:LegalArticle",
    "dct:title": "Mainstreaming renewable energy in the transport sector",
    "dct:description": "In order to mainstream the use of renewable energy in the transport sector, each member state shall set an obligation on fuel suppliers to ensure that the share of renewable energy within the final consumption of energy in the transport sector is at least 14 % by 2030 (minimum share) in accordance with an indicative trajectory set by the member state and calculated in accordance with the methodology set out in this article and in articles 26 and 27. The commission shall assess that obligation, with a view to submitting, by 2023, a legislative proposal to increase it in the event of further substantial costs reductions in the production of renewable energy, where necessary to meet the union's international commitments for decarbonisation, or where justified on the grounds of a significant decrease in energy consumption in the union.",
    "poliana:hasParagraph": [
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "Member states may exempt, or distinguish between, different fuel suppliers and different energy carriers when setting the obligation on the fuel suppliers, ensuring that the varying degrees of maturity and the cost of different technologies are taken into account."
      },
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "For the calculation of the minimum share referred to in the first subparagraph, member states: (a) shall take into account renewable liquid and gaseous transport fuels of non-biological origin also when they are used as intermediate products for the production of conventional fuels; and (b) may take into account recycled carbon fuels."
      },
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "Within the minimum share referred to in the first subparagraph, the contribution of advanced biofuels and biogas produced from the feedstock listed in part a of annex ix as a share of final consumption of energy in the transport sector shall be at least 0,2 % in 2022, at least 1 % in 2025 and at least 3,5 % in 2030."
      },
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "Member states may exempt fuel suppliers supplying fuel in the form of electricity or renewable liquid and gaseous transport fuels of non-biological origin from the requirement to comply with the minimum share of advanced biofuels and biogas produced from the feedstock listed in part a of annex ix with respect to those fuels."
      },
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "When setting the obligation referred to in the first and fourth subparagraphs to ensure the achievement of the share set out therein, member states may do so, inter alia, by means of measures targeting volumes, energy content or greenhouse gas emissions, provided that it is demonstrated that the minimum shares referred to in the first and fourth subparagraphs are achieved."
      }
    ]
  },
  "poliana:hasArticle": {
    "@type": "poliana:LegalArticle",
    "dct:title": "Greenhouse gas emissions savings from renewable liquid and gaseous transport fuels of non-biological origin",
    "dct:description": "The greenhouse gas emissions savings from the use of renewable liquid and gaseous transport fuels of non-biological origin shall be at least 70 % from 1 january 2021.",
    "poliana:hasParagraph": [
      {
        "@type": "poliana:LegalParagraph",
        "dct:description": "By 1 january 2021, the commission shall adopt a delegated act in accordance with article 35 to supplement this directive by establishing appropriate minimum thresholds for greenhouse gas emissions savings of recycled carbon fuels through a life-cycle assessment that takes into account the specificities of each fuel."
      }
    ]
  }
}
```

