{
  "@context": {
    "poliana": "http://data.europa.eu/poli/ontology/poliana#",
    "eli": "http://data.europa.eu/eli/ontology#",
    "xsd": "http://www.w3.org/2001/XMLSchema#"
  },
  "@type": "poliana:LegalActArticle",
  "eli:title": {
    "@value": "specific rules for biofuels, bioliquids and biomass fuels produced from food and feed crops",
    "@language": "en"
  },
  "eli:number": "26",
  "eli:hasPart": [
    {
      "@type": "poliana:LegalActParagraph",
      "eli:number": "1",
      "eli:text": {
        "@value": "for the calculation of a member state's gross final consumption of energy from renewable sources referred to in article 7 and the minimum share referred to in the first subparagraph of article 25(1), the share of biofuels and bioliquids, as well as of biomass fuels consumed in transport, where produced from food and feed crops, shall be no more than one percentage point higher than the share of such fuels in the final consumption of energy in the road and rail transport sectors in 2020 in that member state, with a maximum of 7 % of final consumption of energy in the road and rail transport sectors in that member state.\nwhere that share is below 1 % in a member state, it may be increased to a maximum of 2 % of the final consumption of energy in the road and rail transport sectors.\nmember states may set a lower limit and may distinguish, for the purposes of article 29(1), between different biofuels, bioliquids and biomass fuels produced from food and feed crops, taking into account best available evidence on indirect land-use change impact. member states may, for example, set a lower limit for the share of biofuels, bioliquids and biomass fuels produced from oil crops.\nwhere the share of biofuels and bioliquids, as well as of biomass fuels consumed in transport, produced from food and feed crops in a member state is limited to a share lower than 7 % or a member state decides to limit the share further, that member state may reduce the minimum share referred to in the first subparagraph of article 25(1) accordingly, by a maximum of 7 percentage points.",
        "@language": "en"
      }
    },
    {
      "@type": "poliana:LegalActParagraph",
      "eli:number": "2",
      "eli:text": {
        "@value": "for the calculation of a member state's gross final consumption of energy from renewable sources referred to in article 7 and the minimum share referred to in the first subparagraph of article 25(1), the share of high indirect land-use change-risk biofuels, bioliquids or biomass fuels produced from food and feed crops for which a significant expansion of the production area into land with high-carbon stock is observed shall not exceed the level of consumption of such fuels in that member state in 2019, unless they are certified to be low indirect land-use change-risk biofuels, bioliquids or biomass fuels pursuant to this paragraph.\nfrom 31 december 2023 until 31 december 2030 at the latest, that limit shall gradually decrease to 0 %.\nby 1 february 2019, the commission shall submit to the european parliament and to the council a report on the status of worldwide production expansion of the relevant food and feed crops.\nby 1 february 2019, the commission shall adopt a delegated act in accordance with article 35 to supplement this directive by setting out the criteria for certification of low indirect land-use change-risk biofuels, bioliquids and biomass fuels and for determining the high indirect land-use change-risk feedstock for which a significant expansion of the production area into land with high-carbon stock is observed. the report and the accompanying delegated act shall be based on the best available scientific data.\nby 1 september 2023, the commission shall review the criteria laid down in the delegated act referred to in the fourth subparagraph based on the best available scientific data and shall adopt delegated acts in accordance with article 35 to amend such criteria, where appropriate, and to include a trajectory to gradually decrease the contribution to the union target set out in article 4(1) and the national targets set out in article 6(1) of this directive by the member states, as well as the national contributions referred to in article 20(3), by the member states.",
        "@language": "en"
      }
    }
  ],
  "eli:isPartOf": {
    "@type": "poliana:LegalAct",
    "eli:title": {
      "@value": "Below EU Directive Title",
      "@language": "en"
    },
    "eli:identifier": {
      "@value": "Below_EU_Directive_Identifier",
      "@type": "xsd:string"
    }
  }
}

