pol:EU_32019R0631_Title_0_Chapter_0_Section_0_Article_09 a pol:PolicyArticle ;
    pol:authorises <http://eurovoc.europa.eu/4038> ;
    pol:contains_instrument pol:Edu_Outreach,
        pol:RegulatoryInstr ;
