pol:EU_32019L0944_Title_0_Chapter_4_Section_0_Article_36 a pol:PolicyArticle ;
    pol:addresses pol:Member_States ;
    pol:contains_instrument pol:Edu_Outreach,
        pol:PublicInvt,
        pol:RegulatoryInstr,
